mapper.insert:
insert into mu_admin(
	adm_id, adm_pass, adm_name
) values (
	#{admId}, #{admPass}, #{admName}
)

mapper.select:
select * from mu_board where category = #{category}
and ${type} like CONCAT('%',#{content},'%')
order by board_no desc limit #{begin}, 10

mapper.insert:
insert into mu_board (
	title, writer, content, category
) values(
	#{title}, #{writer}, #{content},#{category}
)

mapper.update:
UPDATE mu_board
 SET title = #{title},content = #{content}
WHERE board_no =  #{boardNo}

mapper.insert:
insert into mu_answer(
	question_no, content
) values(
	#{questionNo}, #{content}
)

mapper.update:
UPDATE mu_board
 SET answer = 'y'
WHERE board_no =  #{boardNo}

UPDATE mu_answer
 SET content = #{content}
WHERE answer_no =  #{answerNo}
