mapper.insert:
insert into mu_board(
	title, writer, content, category
)
values(
	#{title}, #{writer}, #{content}, 'fr'
)

mapper.update:
update mu_board
   set view_cnt = view_cnt + 1
 where board_no = #{boardNo};

update mu_board
   set title = #{title},
       content = #{content}
 where board_no = #{boardNo}

mapper.insert:
insert into mu_comment(
	board_no, comm_writer, comm_content
) values (
	#{boardNo}, #{commWriter}, #{commContent}
)

mapper.update:
update mu_comment
   set comm_content = #{commContent}
 where comm_no = #{commNo}

mapper.select:
select count(*)
  from mu_recomm
 where board_no = #{boardNo}
   and mem_no = #{memNo}

select count(*)
  from mu_recomm
 where board_no = #{boardNo}

mapper.insert:
insert into mu_recomm(
	board_no, mem_no
) values (
	#{boardNo}, #{memNo}
)

mapper.delete:
delete from mu_recomm
 where board_no = #{boardNo}
   and mem_no = #{memNo}
