mapper.select:
		select *
		from mu_company_info
        where com_info_no=#{comInfoNo}
        and mem_no=#{memNo}

		select *
		from mu_company_file cf, mu_company_info ci
		where ci.com_info_no=cf.com_info_no
        and com_file_repr='N'
        and ci.mem_no=#{memNo}
        and ci.com_info_no=#{comInfoNo}

		select *
		from mu_company_file cf, mu_company_info ci
		where ci.com_info_no=cf.com_info_no
        and com_file_repr='Y'
        and ci.mem_no=#{memNo}
        and ci.com_info_no=#{comInfoNo}

mapper.update:
update mu_member
set mem_pass=#{pass}
and mem_name=#{name}
and mem_phone=#{phone}
where mem_no = #{no}

update mu_company
set birth=#{birth}
where gen_no = #{genNo}

update mu_company
set com_addr=#{comAddr}
and com_addr_detail=#{comAddrDetail}
and com_type=#{comType}
where com_no = #{comNo}
